import React from 'react';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      monster: [
        {
          name: 'Ace'
        },
        {
          name: 'Las'
        },
        {
          name: 'granny'
        }
      ]
    };
  }
  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json()).then(users => this.setState({monster:users}));
  }

  render() {
    return (
      <div>
        {this.state.monster.map(monsterName => (
          <h1 key={monsterName.name}>{monsterName.name}</h1>
        ))}
      </div>
    );
  }
}

export default App;
